import React from "react";
import {ChromePicker} from "react-color";

class ColorPicker extends React.Component {

  static defaultProps = {
    initialColor: "purple"
  }
  constructor(props) {
    super(props);
    this.state = {
      colorValue: this.props.initialColor
    };
  }


  handleColorChange = color => {
    this.setState({
      colorValue: color.rgb
    },()=>this.props.colorChanged(color));
  };

  render() {
    const { colorValue } = this.state;

    return (
            <ChromePicker
              color={colorValue}
              onChange={this.handleColorChange}
            />
    );
  }
}

export default ColorPicker;
