import React, { Component } from 'react';
import ColorBox from "./colorBox";
import './palette.css';
import Navbar from './Navbar';

class Palette extends Component {
    constructor(props){
        super(props);
        this.timeoutID = "";
        this.state = {
            deactivateOverflow: false,
            level: 500,
            format: "hex"
        }
    }
    deactivateOverflow = (time) =>{
        clearTimeout(this.timeoutID);
        this.setState({
            deactivateOverflow: true
        }, () => {
            this.timeoutID = setTimeout(() => this.setState({deactivateOverflow: false}),time)
        })
    }

    changeLevel = (level) =>{
        this.setState({
            level
        })
    }
    
    changeFormat = (val) =>{
        this.setState({format: val})
    }

    render() {
        const {colors, paletteName, emoji} = this.props.palette;
        const {level, format} = this.state;
        const colorBoxes = colors[level].map(color =>{
            return <ColorBox background={color[format]} 
                            name={color.name}
                            id={color.id}
                            deactivateOverflow={this.deactivateOverflow}
                            key={color.id}/>
        })
        return (
            <div className={`Palette ${this.state.deactivateOverflow&&"deactivate-overflow"}`}>
                <Navbar level={level} changeLevel={this.changeLevel} handleChange={this.changeFormat}/>
                <div className="Palette-colors">
                    {colorBoxes}
                </div> 
                <footer className="Palette-footer">
                    {paletteName}
                    <span className="emoji">{emoji}</span>
                </footer>
            </div>
        );
    }
}

export default Palette;
