import React, { Component } from 'react';
import  './colorBox.css';
import {CopyToClipboard} from 'react-copy-to-clipboard';
import {Link} from "react-router-dom"
import chroma from "chroma-js"
import { withStyles } from "@material-ui/styles";

const styles = {
    colorBox: {
        width: "20%",
        height: props => props.showingFullPalette?"25%":"50%",
        margin: "0 auto",
        display: "inline-block",
        position: "relative",
        cursor: "pointer",
        marginBottom: "-3.7px",
        "&:hover button": {
            opacity: 1
        }
    },
    copyText: {
        color: props => chroma(props.background).luminance() >= 0.7 ? "rgba(0,0,0,0.5)" : "white"
    },
    colorName: {
        color: props => chroma(props.background).luminance() <= 0.08 ? "white" : "rgba(0,0,0,0.5)"
    },
    seeMore: {
        color: props => chroma(props.background).luminance() >= 0.5 ? "rgba(0,0,0,0.5)":"white",
        backgroundColor: "rgba(255,255,255,0.3)",
        position: "absolute",
        border: "none",
        right: "0px",
        bottom: "0px",
        width: "60px",
        height: "30px",
        textAlign: "center",
        lineHeight: "30px",
        textTransform: "uppercase"
    },
    copyButton: {
        color: props => chroma(props.background).luminance() >= 0.7 ? "rgba(0,0,0,0.5)":"white",
        cursor: "pointer",
        width: "100px",
        height: "30px",
        position: "absolute",
        display: "inline-block",
        top: "50%",
        left: "50%",
        marginTop: "-15px",
        marginLeft: "-50px",
        textAlign: "center",
        outline: "none",
        backgroundColor: "rgba(255,255,255,0.3)",
        fontSize: "1rem",
        lineHeight: "30px",
        textTransform: "uppercase",
        border: "none",
        textDecoration: "none",
        opacity: 0
    },
    boxContent: {
        position: "absolute",
        width: "100",
        left: "0px",
        bottom: "0px",
        color: "black",
        padding: "10px",
        letterSpacing: "1px",
        textTransform: "1px",
        fontSize: "12px"
    },
    copyOverlay: {
        opacity: "0",
        zIndex: "0",
        height: "100%",
        width: "100%",
        transition: "transform 0.6s ease-in-out",
        maxWidth: "100vw"
    },
    showOverlay: {
        opacity: "1",
        transform: "scale(50)",
        zIndex: "10",
        position: "absolute"
    },
    copyMessage: {
        zIndex: "-10",
        position: "fixed",
        left: "0",
        right: "0",
        bottom: "0",
        top: "0",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        fontSize: "2rem",
        transform: "scale(0.1)",
        opacity: "0",
        color: "white"
    },
    showMessage: {
        opacity: "1",
        transform: "scale(1)",
        zIndex: "25",
        transition: "all 0.4s ease-in-out",
        transitionDelay: "0.3s"
    }
}

class colorBox extends Component {
    static defaultProps = {
        showingFullPalette: true
    }
    constructor(props){
        super(props);
        this.state = {
            copied: false
        }
    }
    changeCopyState = () => {
        this.setState({
            copied: true
        }, () => {
            setTimeout(()=>this.setState({copied: false}),1500);
            this.props.deactivateOverflow(2200);
        });
    }
    
    render() {
        const {name,background,paletteId,colorId,showingFullPalette, classes} = this.props;
        const {copied} = this.state;
        const isDarkColor = chroma(background).luminance() <= 0.08;
        const isLightColor = chroma(background).luminance() >= 0.5;
        return (
            <CopyToClipboard text={background} onCopy={this.changeCopyState}>
                <div className={classes.colorBox} style={{background: background}}>
                <div style={{background}} className={`${classes.copyOverlay} ${copied && classes.showMessage}`}/>
                <div className={`${classes.copyMessage} ${this.state.copied&&classes.showOverlay}`}>
                    <h1>Copied!</h1>
                    <p className={classes.copyText}>{this.props.background}</p>
                </div>
                    <div>
                        <div className={classes.boxContent}>
                            <span className={classes.colorName}>{name}</span>
                        </div>
                        <button className={classes.copyButton}>Copy</button>
                    </div>
                    {showingFullPalette && 
                        <Link to={`/palette/${paletteId}/${colorId}`} onClick={(e) => e.stopPropagation()}> 
                            <span className={classes.seeMore}>More</span>
                        </Link>    
                    }
                </div>
            </CopyToClipboard>
        );
    }
}

export default withStyles(styles)(colorBox);
