import React, { Component } from 'react';
import  './colorBox.css';
import {CopyToClipboard} from 'react-copy-to-clipboard';
import {Link} from "react-router-dom"
import chroma from "chroma-js"
import { withStyles } from "@material-ui/styles";

const styles = {
    colorBox: {
        width: "20%",
        height: "25%",
        margin: "0 auto",
        display: "inline-block",
        position: "relative",
        cursor: "pointer",
        marginBottom: "-3.7px"
    },
    copyText: {
        color: props => chroma(props.background).luminance() >= 0.7 ? "rgba(0,0,0,0.5)" : "white"
    },
    colorName: {
        color: props => chroma(props.background).luminance() <= 0.08 ? "white" : "rgba(0,0,0,0.5)"
    },
    seeMore: {
        color: props => chroma(props.background).luminance() >= 0.5 ? "rgba(0,0,0,0.5)":"white",
        backgroundColor: "rgba(255,255,255,0.3)",
        position: "absolute",
        border: "none",
        right: "0px",
        bottom: "0px",
        width: "60px",
        height: "30px",
        textAlign: "center",
        lineHeight: "30px",
        textTransform: "uppercase"
    },
    copyButton: {
        color: props => chroma(props.background).luminance() >= 0.7 ? "rgba(0,0,0,0.5)":"white",
        cursor: "pointer",
        width: "100px",
        height: "30px",
        position: "absolute",
        display: "inline-block",
        top: "50%",
        left: "50%",
        marginTop: "-15px",
        marginLeft: "-50px",
        textAlign: "center",
        outline: "none",
        backgroundColor: "rgba(255,255,255,0.3)",
        fontSize: "1rem",
        lineHeight: "30px",
        textTransform: "uppercase",
        border: "none",
        textDecoration: "none"
    }
}

class colorBox extends Component {
    static defaultProps = {
        showLink: true
    }
    constructor(props){
        super(props);
        this.state = {
            copied: false
        }
    }
    changeCopyState = () => {
        this.setState({
            copied: true
        }, () => {
            setTimeout(()=>this.setState({copied: false}),1500);
            this.props.deactivateOverflow(2200);
        });
    }
    
    render() {
        const {name,background,paletteId,colorId,showLink, classes} = this.props;
        const isDarkColor = chroma(background).luminance() <= 0.08;
        const isLightColor = chroma(background).luminance() >= 0.5;
        return (
            <CopyToClipboard text={background} onCopy={this.changeCopyState}>
                <div className="ColorBox" style={{background: background}}>
                <div style={{background}} className={`copy-overlay ${this.state.copied&&"show"}`}/>
                <div className={`copy-msg ${this.state.copied&&"show"}`}>
                    <h1>Copied!</h1>
                    <p className={classes.copyText}>{this.props.background}</p>
                </div>
                    <div className="copy-container">
                        <div className="box-content">
                            <span className={classes.colorName}>{name}</span>
                        </div>
                        <button className={classes.copyButton}>Copy</button>
                    </div>
                    {showLink && 
                        <Link to={`/palette/${paletteId}/${colorId}`} onClick={(e) => e.stopPropagation()}> 
                            <span className={classes.seeMore}>More</span>
                        </Link>    
                    }
                </div>
            </CopyToClipboard>
        );
    }
}

export default withStyles(styles)(colorBox);
