import React, { Component } from 'react';
import ColorBox from "./colorBox";
import './palette.css';

class Palette extends Component {
    constructor(props){
        super(props);
        this.state = {
            deactivateOverflow: false
        }
    }
    deactivateOverflow = (time) =>{
        this.setState({
            deactivateOverflow: true
        })
    }
    activateOverflow = () =>{
        this.setState({
            deactivateOverflow: false
        })
    }
    render() {
        const colorBoxes = this.props.colors.map(color =>{
            return <ColorBox background={color.color} 
                            name={color.name} 
                            deactivateOverflow={this.deactivateOverflow}
                            activatedeactivateOverflow={this.deactivateOverflow}/>
        })
        return (
            <div className={`Palette ${this.state.deactivateOverflow&&"deactivate-overflow"}`}>
                <div className="Palette-colors">
                    {colorBoxes}
                </div> 
            </div>
        );
    }
}

export default Palette;
