import DRAWER_WIDTH from "../constants";
export default {
    up(){

    },
    down(size){
        const sizes = {
            xsButtons:`${DRAWER_WIDTH+454}px`,
            xs:"576px",
            sm: "768px",
            md: "992px",
            lg: "1200px"
        }
        return `@media (max-width: ${sizes[size]})`;
    }
}
