import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';

class PaletteMetaForm extends Component {

    constructor(props){
        super(props);
        this.state = {
            open: true,
            newPaletteName: ""
        }
    }

    componentDidMount() {
        // custom rule will have name 'isPasswordMatch' 
        ValidatorForm.addValidationRule('isPaletteNameUnique', (value) => {
          return this.props.palettes.every(({paletteName}) => paletteName !== this.state.newPaletteName)
        });
    }

    handleChange = (evt) =>{
        this.setState({[evt.target.name]: evt.target.value})
      } 

    handleClickOpen = () =>{
        this.setState({open: true})
    }

    handleClose = () =>{
        this.setState({open: false})
    }

    render() {
        const { newPaletteName } = this.state;
        return (
            <Dialog open={this.state.open} onClose={this.handleClose} aria-labelledby="form-dialog-title" onClose={this.props.hideForm}>
                <DialogTitle id="form-dialog-title">Choose a Palette Name</DialogTitle>
                <ValidatorForm onSubmit={() => this.props.handleSubmit(newPaletteName)}>
                    <DialogContent>
                    <DialogContentText>
                        Please enter a name for your palette. Make sure it's unique!
                    </DialogContentText>                           
                    <TextValidator
                        fullWidth
                        margin="normal"
                        value={this.state.newPaletteName}
                        name="newPaletteName"
                        label="Palette Name"
                        onChange={this.handleChange}
                        validators={['required','isPaletteNameUnique']}
                        errorMessages={['Enter a palette name','Name already used']}    
                    />
                   
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={this.handleClose} color="primary" onClick={this.props.hideForm}>
                        Cancel
                    </Button>
                    <Button variant="contained" color="primary" type="submit">Save Palette</Button>
                    </DialogActions>
                </ValidatorForm>
            </Dialog>
        );
    }
}

export default PaletteMetaForm;
