import React, { Component } from 'react';
import ColorBox from "./colorBox";
import './palette.css';
import Slider from "rc-slider";
import 'rc-slider/assets/index.css';

class Palette extends Component {
    constructor(props){
        super(props);
        this.timeoutID = "";
        this.state = {
            deactivateOverflow: false,
            level: 500
        }
    }
    deactivateOverflow = (time) =>{
        clearTimeout(this.timeoutID);
        this.setState({
            deactivateOverflow: true
        }, () => {
            this.timeoutID = setTimeout(() => this.setState({deactivateOverflow: false}),time)
        })
    }

    changeLevel = (level) =>{
        this.setState({
            level
        })
    }
    
    render() {
        const {colors} = this.props.palette;
        const {level} = this.state;
        const colorBoxes = colors[level].map(color =>{
            return <ColorBox background={color.hex} 
                            name={color.name}
                            deactivateOverflow={this.deactivateOverflow}/>
        })
        return (
            <div className={`Palette ${this.state.deactivateOverflow&&"deactivate-overflow"}`}>
                <h1>`${this.state.deactivateOverflow.toString()}`</h1>
                <div className="Slider">
                    <Slider defaultValue={level} 
                            min={100} 
                            max={900} 
                            step={100}
                            onAfterChange={this.changeLevel}/>
                </div>
                <div className="Palette-colors">
                    {colorBoxes}
                </div> 
            </div>
        );
    }
}

export default Palette;
