import sizes from "./sizes";

const styles = {
    root: {
        height: "100%",
        display: "flex",
        flexDirection: "row", 
        flexWrap: "wrap", 
        justifyContent: "flex-start",
        alignContent: "flex-start",
        [sizes.down("xs")]: {
            marginTop: "5px"
        }
    }
}

export default styles;
